import React, { useState } from 'react';
import {
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  TextInput,
  Image,
  StatusBar,
  StyleSheet,
  SafeAreaView,
  Dimensions,
  Platform,
} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { BlurView } from 'expo-blur';
import { MaterialIcons } from '@expo/vector-icons';
import ChatbotDialogRN from './ChatbotDialogRN';

const { width, height } = Dimensions.get('window');

type Screen = 'home' | 'explore' | 'trips' | 'budget' | 'profile';

const COLORS = {
  deepGreen: '#2C6E49',
  mediumGreen: '#4C956C', 
  cream: '#FEFEE3',
  coral: '#FFC9B9',
  gold: '#D68C45',
};

export default function App() {
  const [currentScreen, setCurrentScreen] = useState<Screen>('home');
  const [isChatbotOpen, setIsChatbotOpen] = useState(false);
  const [isOfflineMode, setIsOfflineMode] = useState(false);

  const upcomingTrips = [
    {
      destination: "Tokyo, Japan",
      dates: "Jan 10-20, 2025",
      image: "https://images.unsplash.com/photo-1542909356-08e5625abea7?w=400",
      status: "Planning",
      price: 850
    },
    {
      destination: "Swiss Alps",
      dates: "Mar 5-12, 2025", 
      image: "https://images.unsplash.com/photo-1615472767332-e5615c7e617a?w=400",
      status: "Saved",
      price: 1200
    }
  ];

  const quickActions = [
    { icon: 'navigation' as const, label: 'Navigation', color: COLORS.mediumGreen },
    { icon: 'camera-alt' as const, label: 'Travel Journal', color: COLORS.coral },
    { icon: 'people' as const, label: 'Local Guides', color: COLORS.gold },
    { icon: 'security' as const, label: 'Travel Safety', color: COLORS.deepGreen },
  ];

  const renderHomeScreen = () => (
    <ScrollView
      style={styles.screenContainer}
      showsVerticalScrollIndicator={false}
      contentContainerStyle={[styles.scrollContent, { paddingBottom: 120 }]}
    >
      {/* Header */}
      <View style={styles.header}>
        <View>
          <Text style={styles.headerTitle}>Discover</Text>
          <Text style={styles.headerSubtitle}>Amazing Places</Text>
          <View style={styles.statusIndicator}>
            <View style={styles.statusDot} />
            <Text style={styles.statusText}>Live Weather & Updates</Text>
          </View>
        </View>
        <TouchableOpacity style={styles.notificationButton}>
          <MaterialIcons name="notifications" size={24} color={COLORS.cream} />
        </TouchableOpacity>
      </View>

      {/* Featured Adventure Card */}
      <LinearGradient
        colors={[COLORS.coral, COLORS.gold]}
        style={styles.featuredCard}
        start={{ x: 0, y: 0 }}
        end={{ x: 1, y: 1 }}
      >
        <View style={styles.featuredCardContent}>
          <View style={styles.premiumBadge}>
            <MaterialIcons name="stars" size={16} color={COLORS.gold} />
            <Text style={styles.premiumText}>Premium Experience</Text>
          </View>
          <Text style={styles.featuredTitle}>Unlock Exclusive</Text>
          <Text style={styles.featuredTitle}>Travel Destinations</Text>
          <TouchableOpacity style={styles.startButton}>
            <Text style={styles.startButtonText}>Start Journey</Text>
          </TouchableOpacity>
        </View>
      </LinearGradient>

      {/* Search Bar */}
      <View style={styles.searchContainer}>
        <BlurView intensity={80} style={styles.searchBar}>
          <View style={styles.searchIconContainer}>
            <MaterialIcons name="location-on" size={20} color={COLORS.cream} />
          </View>
          <TextInput
            placeholder="Where would you like to go?"
            placeholderTextColor={`${COLORS.deepGreen}99`}
            style={styles.searchInput}
          />
          <TouchableOpacity style={styles.searchButton}>
            <Text style={styles.searchButtonText}>Search</Text>
          </TouchableOpacity>
        </BlurView>
      </View>

      {/* Sync Status */}
      <BlurView intensity={80} style={styles.syncCard}>
        <View style={styles.syncContent}>
          <View style={styles.syncLeft}>
            <View style={[styles.syncIcon, { backgroundColor: isOfflineMode ? `${COLORS.gold}33` : `${COLORS.mediumGreen}33` }]}>
              <MaterialIcons 
                name={isOfflineMode ? "wifi-off" : "cloud-upload"} 
                size={20} 
                color={isOfflineMode ? COLORS.gold : COLORS.mediumGreen} 
              />
            </View>
            <View>
              <Text style={styles.syncTitle}>
                {isOfflineMode ? 'Offline Mode Active' : 'All Data Synced'}
              </Text>
              <Text style={styles.syncSubtitle}>
                Last sync: {new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
              </Text>
            </View>
          </View>
          <TouchableOpacity 
            style={styles.syncButton}
            onPress={() => setIsOfflineMode(!isOfflineMode)}
          >
            <MaterialIcons name={isOfflineMode ? "wifi" : "file-download"} size={16} color={COLORS.cream} />
          </TouchableOpacity>
        </View>
      </BlurView>

      {/* Quick Actions */}
      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Quick Actions</Text>
        <View style={styles.quickActionsGrid}>
          {quickActions.map((action, index) => (
            <TouchableOpacity key={index} style={styles.actionCard}>
              <BlurView intensity={80} style={styles.actionCardContent}>
                <View style={[styles.actionIcon, { backgroundColor: action.color }]}>
                  <MaterialIcons name={action.icon} size={24} color={COLORS.cream} />
                </View>
                <Text style={styles.actionLabel}>{action.label}</Text>
              </BlurView>
            </TouchableOpacity>
          ))}
        </View>
      </View>

      {/* Featured Destinations */}
      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Trending Destinations</Text>
        <View style={styles.destinationsGrid}>
          {upcomingTrips.map((trip, index) => (
            <TouchableOpacity key={index} style={styles.destinationCard}>
              <BlurView intensity={80} style={styles.destinationCardContent}>
                <View style={styles.destinationImageContainer}>
                  <Image source={{ uri: trip.image }} style={styles.destinationImage} />
                  <LinearGradient
                    colors={['transparent', 'rgba(0,0,0,0.6)']}
                    style={styles.destinationOverlay}
                  />
                  <TouchableOpacity style={styles.favoriteButton}>
                    <MaterialIcons name="star" size={16} color={COLORS.gold} />
                  </TouchableOpacity>
                  <View style={styles.destinationInfo}>
                    <Text style={styles.destinationName}>{trip.destination}</Text>
                    <Text style={styles.destinationDates}>{trip.dates}</Text>
                  </View>
                </View>
                <View style={styles.destinationFooter}>
                  <Text style={styles.destinationPrice}>${trip.price}</Text>
                  <View style={[styles.statusBadge, { backgroundColor: COLORS.coral }]}>
                    <Text style={styles.statusBadgeText}>{trip.status}</Text>
                  </View>
                </View>
              </BlurView>
            </TouchableOpacity>
          ))}
        </View>
      </View>
    </ScrollView>
  );

  const renderCurrentScreen = () => {
    switch (currentScreen) {
      case 'home': return renderHomeScreen();
      case 'explore': return renderExploreScreen();
      case 'trips': return renderTripsScreen();
      case 'budget': return renderBudgetScreen();
      case 'profile': return renderProfileScreen();
      default: return renderHomeScreen();
    }
  };

  const renderExploreScreen = () => (
    <View style={styles.screenContainer}>
      <Text style={styles.screenTitle}>Explore</Text>
      <Text style={styles.screenSubtitle}>Coming soon...</Text>
    </View>
  );

  const renderTripsScreen = () => {
    const currentTrip = {
      id: 1,
      name: "Tokyo Adventure",
      destination: "Tokyo, Japan",
      startDate: "2025-01-10",
      endDate: "2025-01-20",
      daysRemaining: 100,
      progress: 0.3,
      itinerary: [
        {
          day_number: 1,
          date: "2025-01-10",
          activities: [
            { time: "09:00", name: "Arrive at Haneda Airport", location: "Tokyo Haneda Airport", booking_ref: "NH123" },
            { time: "14:00", name: "Check-in Hotel", location: "Shibuya Grand Hotel", booking_ref: "HB456" },
            { time: "18:00", name: "Shibuya Crossing Experience", location: "Shibuya", booking_ref: null }
          ],
          daily_notes: "First day in Tokyo - take it easy due to jet lag"
        },
        {
          day_number: 2,
          date: "2025-01-11", 
          activities: [
            { time: "09:00", name: "Tokyo National Museum", location: "Ueno", booking_ref: "TM789" },
            { time: "13:00", name: "Lunch in Harajuku", location: "Harajuku", booking_ref: null },
            { time: "15:00", name: "Meiji Shrine Visit", location: "Shibuya", booking_ref: null }
          ],
          daily_notes: "Cultural exploration day"
        }
      ],
      bookings: {
        flights: [
          { type: "Departure", airline: "ANA", flight: "NH123", date: "Jan 10", time: "06:30", from: "LAX", to: "HND" },
          { type: "Return", airline: "ANA", flight: "NH124", date: "Jan 20", time: "18:00", from: "HND", to: "LAX" }
        ],
        hotels: [
          { name: "Shibuya Grand Hotel", checkin: "Jan 10", checkout: "Jan 15", nights: 5, room: "Deluxe Twin" },
          { name: "Kyoto Traditional Inn", checkin: "Jan 15", checkout: "Jan 20", nights: 5, room: "Tatami Suite" }
        ],
        activities: [
          { name: "Tokyo National Museum", date: "Jan 11", time: "09:00", price: "$15" },
          { name: "Mount Fuji Day Trip", date: "Jan 13", time: "07:00", price: "$120" }
        ]
      },
      journal: [
        { type: "photo", url: "https://images.unsplash.com/photo-1542909356-08e5625abea7?w=300", caption: "Amazing ramen in Shibuya!", date: "Jan 11" },
        { type: "video", url: "https://images.unsplash.com/photo-1513407030348-c983a97b98d8?w=300", caption: "Sunrise at Tokyo Tower", date: "Jan 10" }
      ]
    };

    return (
      <ScrollView 
        style={styles.screenContainer}
        showsVerticalScrollIndicator={false}
        contentContainerStyle={[styles.scrollContent, { paddingBottom: 120 }]}
      >
        {/* Emergency Services Button */}
        <TouchableOpacity style={styles.emergencyButton}>
          <MaterialIcons name="emergency" size={20} color={COLORS.cream} />
          <Text style={styles.emergencyText}>Emergency Services</Text>
          <MaterialIcons name="phone" size={16} color={COLORS.cream} />
        </TouchableOpacity>

        {/* Current Trip Overview */}
        <LinearGradient
          colors={[COLORS.deepGreen, COLORS.mediumGreen]}
          style={styles.tripOverviewCard}
          start={{ x: 0, y: 0 }}
          end={{ x: 1, y: 1 }}
        >
          <View style={styles.tripHeader}>
            <Text style={styles.tripName}>{currentTrip.name}</Text>
            <Text style={styles.tripDestination}>{currentTrip.destination}</Text>
          </View>
          
          <View style={styles.tripProgress}>
            <Text style={styles.countdownText}>{currentTrip.daysRemaining} days to go</Text>
            <View style={styles.progressBarContainer}>
              <View style={[styles.progressBar, { width: `${currentTrip.progress * 100}%` }]} />
            </View>
            <Text style={styles.tripDates}>{currentTrip.startDate} - {currentTrip.endDate}</Text>
          </View>
        </LinearGradient>

        {/* Interactive Itinerary */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Daily Itinerary</Text>
          {currentTrip.itinerary.map((day, index) => (
            <View key={index} style={styles.dayCard}>
              <BlurView intensity={80} style={styles.dayCardContent}>
                <View style={styles.dayHeader}>
                  <View style={styles.dayNumber}>
                    <Text style={styles.dayNumberText}>Day {day.day_number}</Text>
                  </View>
                  <Text style={styles.dayDate}>{new Date(day.date).toLocaleDateString('en-US', { weekday: 'long', month: 'short', day: 'numeric' })}</Text>
                </View>
                
                <View style={styles.activitiesList}>
                  {day.activities.map((activity, actIndex) => (
                    <View key={actIndex} style={styles.activityItem}>
                      <View style={styles.activityTime}>
                        <Text style={styles.timeText}>{activity.time}</Text>
                      </View>
                      <View style={styles.activityDetails}>
                        <Text style={styles.activityName}>{activity.name}</Text>
                        <Text style={styles.activityLocation}>{activity.location}</Text>
                        {activity.booking_ref && (
                          <Text style={styles.bookingRef}>Ref: {activity.booking_ref}</Text>
                        )}
                      </View>
                    </View>
                  ))}
                </View>
                
                {day.daily_notes && (
                  <View style={styles.notesSection}>
                    <MaterialIcons name="note" size={16} color={COLORS.mediumGreen} />
                    <Text style={styles.notesText}>{day.daily_notes}</Text>
                  </View>
                )}
              </BlurView>
            </View>
          ))}
        </View>

        {/* Quick Access to Bookings */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Bookings Overview</Text>
          
          {/* Flights */}
          <View style={styles.bookingCategory}>
            <View style={styles.bookingHeader}>
              <MaterialIcons name="flight" size={20} color={COLORS.mediumGreen} />
              <Text style={styles.bookingCategoryTitle}>Flights</Text>
            </View>
            {currentTrip.bookings.flights.map((flight, index) => (
              <BlurView key={index} intensity={80} style={styles.bookingCard}>
                <View style={styles.bookingInfo}>
                  <Text style={styles.bookingPrimary}>{flight.airline} {flight.flight}</Text>
                  <Text style={styles.bookingSecondary}>{flight.from}  {flight.to}</Text>
                  <Text style={styles.bookingTime}>{flight.date} at {flight.time}</Text>
                </View>
              </BlurView>
            ))}
          </View>

          {/* Hotels */}
          <View style={styles.bookingCategory}>
            <View style={styles.bookingHeader}>
              <MaterialIcons name="hotel" size={20} color={COLORS.coral} />
              <Text style={styles.bookingCategoryTitle}>Hotels</Text>
            </View>
            {currentTrip.bookings.hotels.map((hotel, index) => (
              <BlurView key={index} intensity={80} style={styles.bookingCard}>
                <View style={styles.bookingInfo}>
                  <Text style={styles.bookingPrimary}>{hotel.name}</Text>
                  <Text style={styles.bookingSecondary}>{hotel.room}  {hotel.nights} nights</Text>
                  <Text style={styles.bookingTime}>{hotel.checkin} - {hotel.checkout}</Text>
                </View>
              </BlurView>
            ))}
          </View>

          {/* Activities */}
          <View style={styles.bookingCategory}>
            <View style={styles.bookingHeader}>
              <MaterialIcons name="local-activity" size={20} color={COLORS.gold} />
              <Text style={styles.bookingCategoryTitle}>Activities</Text>
            </View>
            {currentTrip.bookings.activities.map((activity, index) => (
              <BlurView key={index} intensity={80} style={styles.bookingCard}>
                <View style={styles.bookingInfo}>
                  <Text style={styles.bookingPrimary}>{activity.name}</Text>
                  <Text style={styles.bookingSecondary}>{activity.date} at {activity.time}</Text>
                  <Text style={styles.bookingPrice}>{activity.price}</Text>
                </View>
              </BlurView>
            ))}
          </View>
        </View>

        {/* Trip Journal/Multimedia Feed */}
        <View style={styles.section}>
          <View style={styles.journalHeader}>
            <Text style={styles.sectionTitle}>Trip Journal</Text>
            <TouchableOpacity style={styles.addMediaButton}>
              <MaterialIcons name="add-a-photo" size={20} color={COLORS.cream} />
            </TouchableOpacity>
          </View>
          
          <View style={styles.journalGrid}>
            {currentTrip.journal.map((item, index) => (
              <TouchableOpacity key={index} style={styles.journalItem}>
                <Image source={{ uri: item.url }} style={styles.journalMedia} />
                <LinearGradient
                  colors={['transparent', 'rgba(0,0,0,0.8)']}
                  style={styles.journalOverlay}
                >
                  <View style={styles.journalInfo}>
                    <MaterialIcons 
                      name={item.type === 'photo' ? 'photo' : 'videocam'} 
                      size={16} 
                      color={COLORS.cream} 
                    />
                    <Text style={styles.journalDate}>{item.date}</Text>
                  </View>
                  <Text style={styles.journalCaption}>{item.caption}</Text>
                </LinearGradient>
              </TouchableOpacity>
            ))}
          </View>
        </View>
      </ScrollView>
    );
  };

  const renderBudgetScreen = () => (
    <View style={styles.screenContainer}>
      <Text style={styles.screenTitle}>Budget</Text>
      <Text style={styles.screenSubtitle}>Coming soon...</Text>
    </View>
  );

  const renderProfileScreen = () => (
    <View style={styles.screenContainer}>
      <Text style={styles.screenTitle}>Profile</Text>
      <Text style={styles.screenSubtitle}>Coming soon...</Text>
    </View>
  );

  return (
    <View style={styles.container}>
      <StatusBar barStyle="light-content" backgroundColor={COLORS.deepGreen} />
      
      {/* Background */}
      <LinearGradient
        colors={[`${COLORS.cream}E6`, `${COLORS.coral}CC`, `${COLORS.mediumGreen}B3`]}
        style={styles.backgroundGradient}
        start={{ x: 0, y: 0 }}
        end={{ x: 1, y: 1 }}
      >
        {/* Floating Orbs */}
        <View style={[styles.floatingOrb, styles.orb1, { backgroundColor: `${COLORS.coral}33` }]} />
        <View style={[styles.floatingOrb, styles.orb2, { backgroundColor: `${COLORS.gold}26` }]} />
        <View style={[styles.floatingOrb, styles.orb3, { backgroundColor: `${COLORS.mediumGreen}1A` }]} />

        {/* Safe Area for Status Bar */}
        <SafeAreaView style={styles.safeAreaTop}>
          <View style={styles.statusBar}>
            <Text style={styles.timeText}>09:41</Text>
            <View style={styles.statusIcons}>
              <View style={styles.signalBars}>
                {[1,2,3,4].map(i => <View key={i} style={styles.signalBar} />)}
              </View>
              <MaterialIcons name="wifi" size={16} color={COLORS.cream} />
              <View style={styles.battery}>
                <View style={styles.batteryLevel} />
              </View>
            </View>
          </View>
        </SafeAreaView>

        {/* Main Content */}
        <View style={styles.mainContent}>
          {renderCurrentScreen()}
        </View>

        {/* Floating Chat Button */}
        <TouchableOpacity 
          style={styles.chatButton}
          onPress={() => setIsChatbotOpen(true)}
        >
          <LinearGradient
            colors={[COLORS.gold, COLORS.coral]}
            style={styles.chatButtonGradient}
            start={{ x: 0, y: 0 }}
            end={{ x: 1, y: 1 }}
          >
            <MaterialIcons name="chat" size={28} color={COLORS.deepGreen} />
          </LinearGradient>
        </TouchableOpacity>

        {/* Bottom Navigation - Always Visible */}
        <View style={styles.bottomNavContainer}>
          <SafeAreaView style={styles.safeAreaBottom}>
            <BlurView intensity={80} style={styles.bottomNav}>
              <View style={styles.navContent}>
                {[
                  { id: 'home', icon: 'home' as const, label: 'Home' },
                  { id: 'trips', icon: 'map' as const, label: 'Trips' },
                  { id: 'budget', icon: 'account-balance-wallet' as const, label: 'Budget' },
                  { id: 'explore', icon: 'explore' as const, label: 'Explore' },
                  { id: 'profile', icon: 'person' as const, label: 'Profile' }
                ].map((tab) => (
                  <TouchableOpacity
                    key={tab.id}
                    style={[
                      styles.navTab,
                      currentScreen === tab.id && { backgroundColor: COLORS.coral }
                    ]}
                    onPress={() => setCurrentScreen(tab.id as Screen)}
                  >
                    <MaterialIcons 
                      name={tab.icon} 
                      size={20} 
                      color={currentScreen === tab.id ? COLORS.deepGreen : `${COLORS.cream}99`} 
                    />
                  </TouchableOpacity>
                ))}
              </View>
            </BlurView>
          </SafeAreaView>
        </View>

        {/* Chatbot Dialog */}
        <ChatbotDialogRN 
          isOpen={isChatbotOpen} 
          onClose={() => setIsChatbotOpen(false)} 
        />
      </LinearGradient>
    </View>
  );
}

